//创建时必须要有输入端，即便只是一个Null


vector pos = {4,0,0};

int p0 = addpoint(0,{0,0,0});//创建一个点在0,0,0位置
int p1 = addpoint(0,pos);//该函数返回所创建点的序号(在vex运行这段代码时产生的)
int p2 = addpoint(0,{0,3,0});//这个序号不一定是最终序号，但可以用来连线，加属性
int p3 = addpoint(0,{0,-2.5,0});

int prim = addprim(0, 'sphere',   p3);       //sphere 球  //球位置我改了
           addprim(0, 'polyline', p0,p3);    //polyline 线
           addprim(0, 'poly',     p0,p1,p2); //poly 面

setprimintrinsic(0,'transform',prim,matrix3(ident())*.5 );
