function int[] my_neighbours1(int ptnum)
{
    int prims[]= pointprims(0,ptnum);

    int pts[];
    int nb[];
    int npt;
    int ptprev = -1;
    int ptnext = -1;

    foreach(int prim; prims)
    {
        pts = primpoints(0,prim);
        npt = len(pts);
        int myIndex = find(pts,ptnum);

        if( myIndex == 0 )
        {
            ptprev = pts[npt-1];
            ptnext = pts[myIndex+1];
        }
        else if( myIndex == npt-1 )
        {
            ptprev = pts[myIndex-1];
            ptnext = pts[0];
        }
        else
        {
        ptprev = pts[myIndex-1];
        ptnext = pts[myIndex+1];
        }
         if( find(nb, ptprev) < 0 )
            append(nb,ptprev);
         if( find(nb, ptnext) < 0 )
            append(nb,ptnext);
     }
     return nb;
}

i[]@nb = my_neighbours1(@ptnum);
